import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import "./App.css";

// K·∫øt n·ªëi t·ªõi SocketIO server (Backend)
const socket = io("http://localhost:5000");

export default function App() {
  const [frame, setFrame] = useState(null);
  const [counts, setCounts] = useState({});
  // Th√™m state cho ƒë·∫øm h∆∞·ªõng, kh·ªüi t·∫°o v·ªõi gi√° tr·ªã m·∫∑c ƒë·ªãnh
  const [crossCounts, setCrossCounts] = useState({ up: 0, down: 0 }); 
  const [updatedKeys, setUpdatedKeys] = useState([]);

  useEffect(() => {
    // L·∫Øng nghe s·ª± ki·ªán "frame" t·ª´ server
    socket.on("frame", (data) => {
      // C·∫≠p nh·∫≠t frame (Base64 data)
      setFrame("data:image/jpeg;base64," + data.frame);

      // T√¨m keys thay ƒë·ªïi ƒë·ªÉ th√™m class glow cho ƒë·∫øm theo class
      const changedKeys = Object.keys(data.counts).filter(
        (key) => counts[key] !== data.counts[key]
      );
      setUpdatedKeys(changedKeys);

      setCounts(data.counts);

      // C·∫≠p nh·∫≠t ƒë·∫øm h∆∞·ªõng
      if (data.cross_counts) {
        setCrossCounts(data.cross_counts);
      }

      // Remove glow class sau 800ms
      setTimeout(() => setUpdatedKeys([]), 800);
    });

    // Dependency array ƒë·ªÉ useEffect kh√¥ng ch·∫°y l·∫°i li√™n t·ª•c m√† ch·ªâ khi counts ho·∫∑c crossCounts thay ƒë·ªïi
  }, [counts, crossCounts]); 

  return (
    <div className="app-container">
      <h1>üöó Vehicle & Pedestrian Tracker</h1>

      <div className="content">
        <div className="stream-container">
          {frame ? (
            <img src={frame} alt="stream" />
          ) : (
            <p className="waiting-text">Waiting for stream...</p>
          )}
        </div>

        <div className="counts-container">
          <h2>üìä Total Objects</h2>
          <ul>
            {/* Hi·ªÉn th·ªã ƒë·∫øm theo class (car, person,...) */}
            {Object.entries(counts).map(([k, v]) => (
              <li
                key={k}
                className={updatedKeys.includes(k) ? "count-updated" : ""}
              >
                {k}: <span>{v}</span>
              </li>
            ))}
          </ul>
          
          <hr /> 
          
          <h2>üìç Directional Counts</h2>
          <ul className="directional-counts">
             {/* Hi·ªÉn th·ªã ƒë·∫øm h∆∞·ªõng */}
             <li className={crossCounts.up > 0 ? "cross-updated" : ""}>
                ‚¨ÜÔ∏è UP (V√†o): <span>{crossCounts.up}</span>
            </li>
            <li className={crossCounts.down > 0 ? "cross-updated" : ""}>
                ‚¨áÔ∏è DOWN (Ra): <span>{crossCounts.down}</span>
            </li>
          </ul>
          
        </div>
      </div>
    </div>
  );
}
