import React, { useEffect, useRef, useState } from "react";
import { io } from "socket.io-client";

function App() {
  const [counts, setCounts] = useState({});
  const imgRef = useRef(null);

  useEffect(() => {
    // kết nối tới server Python SocketIO
    const socket = io("http://192.168.1.10:5000"); // IP máy chạy Python server

    socket.on("connect", () => {
      console.log("Connected to server");
    });

    socket.on("frame", (data) => {
      // Cập nhật frame camera
      if (imgRef.current) {
        imgRef.current.src = "data:image/jpeg;base64," + data.frame;
      }
      // Cập nhật số lượng phương tiện
      setCounts(data.counts);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  return (
    <div>
      <h1>Vehicle Detection Frontend</h1>
      <div>
        <img ref={imgRef} alt="Vehicle Detection" style={{ width: "100%", maxWidth: "640px" }} />
      </div>
      <div>
        <h3>Vehicle Counts:</h3>
        <ul>
          {Object.entries(counts).map(([key, value]) => (
            <li key={key}>{key}: {value}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
