import React, { useState } from "react";

export default function Login({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = () => {
    const users = {
      admin: { pass: "admin123", role: "admin" },
      baove: { pass: "123456", role: "operator" },
      guest: { pass: "123456", role: "viewer" }
    };
    if (users[username] && users[username].pass === password) {
      localStorage.setItem("role", users[username].role);
      onLogin(users[username].role);
    } else {
      alert("Sai tài khoản hoặc mật khẩu!");
    }
  };

  return (
    <div style={{display:"grid",placeItems:"center",height:"100vh",background:"#222",color:"white"}}>
      <div style={{background:"white",color:"black",padding:40,borderRadius:12,width:350,textAlign:"center"}}>
        <h2>Đăng Nhập Hệ Thống ANPR</h2>
        <input placeholder="Tên đăng nhập" value={username} onChange={e=>setUsername(e.target.value)} style={{width:"100%",padding:12,margin:"10px 0"}}/><br/>
        <input type="password" placeholder="Mật khẩu" value={password} onChange={e=>setPassword(e.target.value)} style={{width:"100%",padding:12,margin:"10px 0"}}/><br/>
        <button onClick={handleLogin} style={{width:"100%",padding:14,background:"#0066ff",color:"white",border:"none",borderRadius:8}}>Đăng Nhập</button>
        <div style={{marginTop:20,fontSize:13}}>
          <p>admin / admin123 → Admin</p>
          <p>baove / 123456 → Bảo vệ</p>
          <p>guest / 123456 → Khách</p>
        </div>
      </div>
    </div>
  );
}
