import React, { useState, useEffect } from "react";

export default function Dashboard({ role, socket }) {
  const [frame, setFrame] = useState("");
  const [counts, setCounts] = useState({ up: 0, down: 0 });
  const [plates, setPlates] = useState({});
  const [threshold, setThreshold] = useState(0.3);

  useEffect(() => {
    socket.on("frame", data => {
      setFrame("data:image/jpeg;base64," + data.frame);
      setCounts(data.counts);
      setPlates(data.plates);
    });
  }, [socket]);

  const sendThreshold = () => {
    socket.emit("update_conf_threshold", { threshold });
  };

  const resetCounter = () => {
    socket.emit("reset_counter");
  };

  return (
    <div style={{padding:20, fontFamily:"Arial"}}>
        <div style={{ marginBottom: "20px", textAlign: "right" }}>
    <span style={{ marginRight: "15px", fontWeight: "bold" }}>
        Đã đăng nhập: {localStorage.getItem("username") || "Guest"}
    </span>
    <button
        onClick={() => {
        localStorage.clear();           // xóa hết thông tin đăng nhập
        window.location.reload();      // reload lại trang → quay về Login
        }}
        style={{
        padding: "8px 16px",
        background: "#d32f2f",
        color: "white",
        border: "none",
        borderRadius: "4px",
        cursor: "pointer"
        }}
    >
        Đăng xuất
    </button>
    
    </div>
      <h1>HỆ THỐNG NHẬN DIỆN PHƯƠNG TIỆN & BIỂN SỐ</h1>
      <p>Đăng nhập với vai trò: <b>{role.toUpperCase()}</b></p>

      {frame && <img src={frame} alt="stream" style={{width:"100%", maxWidth:800}}/>}

      <div style={{marginTop:20}}>
        <h2>Bộ đếm: UP {counts.up} | DOWN {counts.down}</h2>

        {role === "admin" && (
          <>
            <input type="range" min="0.1" max="0.9" step="0.05" value={threshold} onChange={e=>setThreshold(e.target.value)}/>
            <button onClick={sendThreshold}>Cập nhật ngưỡng</button>
            <button onClick={resetCounter} style={{marginLeft:10, background:"red", color:"white"}}>Reset bộ đếm</button>
          </>
        )}

        <h3>Biển số gần nhất:</h3>
        <ul>{Object.entries(plates).map(([k,v]) => <li key={k}><b>{k.replace("LP_","ID ")}</b>: {v}</li>)}</ul>
      </div>
    </div>
  );
}
