import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import "./App.css";

const socket = io("http://localhost:5000");

export default function App() {
  const [frame, setFrame] = useState(null);
  const [counts, setCounts] = useState({});
  const [updatedKeys, setUpdatedKeys] = useState([]);

  useEffect(() => {
    socket.on("frame", (data) => {
      setFrame("data:image/jpeg;base64," + data.frame);

      // Tìm keys thay đổi để thêm class glow
      const changedKeys = Object.keys(data.counts).filter(
        (key) => counts[key] !== data.counts[key]
      );
      setUpdatedKeys(changedKeys);

      setCounts(data.counts);

      // Remove glow class sau 800ms
      setTimeout(() => setUpdatedKeys([]), 800);
    });
  }, [counts]);

  return (
    <div className="app-container">
      <h1>Vehicle Detection</h1>

      <div className="stream-container">
        {frame ? (
          <img src={frame} alt="stream" />
        ) : (
          <p className="waiting-text">Waiting for stream...</p>
        )}
      </div>

      <div className="counts-container">
        <h2>Counts</h2>
        <ul>
          {Object.entries(counts).map(([k, v]) => (
            <li
              key={k}
              className={updatedKeys.includes(k) ? "count-updated" : ""}
            >
              {k}: <span>{v}</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
