// frontend-react/src/components/History.jsx
import React, { useState, useEffect } from "react";

// === COMPONENT TOAST SIÊU ĐẸP ===
function Toast({ message, type = "success", onClose }) {
  useEffect(() => {
    const timer = setTimeout(() => onClose(), 3000); // tự tắt sau 3 giây
    return () => clearTimeout(timer);
  }, [onClose]);

  const bgColor = type === "success" ? "#4caf50" : type === "error" ? "#f44336" : "#ff9800";

  return (
    <div style={{
      position: "fixed",
      top: "20px",
      right: "20px",
      background: bgColor,
      color: "white",
      padding: "16px 24px",
      borderRadius: 12,
      boxShadow: "0 8px 25px rgba(0,0,0,0.3)",
      zIndex: 10000,
      fontWeight: "bold",
      fontSize: 16,
      display: "flex",
      alignItems: "center",
      gap: 10,
      animation: "slideIn 0.4s ease-out"
    }}>
      <span>{type === "success" ? "Check" : type === "error" ? "Error" : "Warning"}</span>
      <span>{message}</span>
    </div>
  );
}

export default function History() {
  const [records, setRecords] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editPlate, setEditPlate] = useState("");
  const [toast, setToast] = useState(null); // {message, type}

  const showToast = (message, type = "success") => {
    setToast({ message, type });
  };

  useEffect(() => {
    loadRecords();
  }, []);

  const loadRecords = () => {
    fetch("http://localhost:5000/api/records")
      .then(res => res.json())
      .then(data => setRecords(data))
      .catch(() => showToast("Lỗi tải dữ liệu", "error"));
  };

  const handleDelete = (id) => {
    if (!window.confirm("Xóa bản ghi này?")) return;

    fetch(`http://localhost:5000/api/records/${id}`, { method: "DELETE" })
      .then(res => res.json())
      .then(result => {
        if (result.success) {
          showToast("Đã xóa thành công!", "success");
          loadRecords();
        } else {
          showToast(result.message || "Xóa thất bại", "error");
        }
      })
      .catch(() => showToast("Lỗi kết nối server", "error"));
  };

  const startEdit = (record) => {
    setEditingId(record.id);
    setEditPlate(record.plate_number || "");
  };

  const cancelEdit = () => {
    setEditingId(null);
    setEditPlate("");
  };

  const saveEdit = (id) => {
    const newPlate = editPlate.trim().toUpperCase();
    if (!newPlate) {
      showToast("Biển số không được để trống!", "error");
      return;
    }

    fetch(`http://localhost:5000/api/records/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ plate_number: newPlate })
    })
      .then(res => {
        if (!res.ok) throw new Error("Lỗi server");
        return res.json();
      })
      .then(result => {
        if (result.success) {
          showToast(`Cập nhật thành công: ${newPlate}`, "success");
          setEditingId(null);
          loadRecords();
        }
      })
      .catch(() => showToast("Không thể cập nhật biển số", "error"));
  };

  return (
    <div style={{ padding: 20, fontFamily: "Arial", background: "#f4f4f4", minHeight: "100vh", position: "relative" }}>
      {/* TOAST */}
      {toast && <Toast message={toast.message} type={toast.type} onClose={() => setToast(null)} />}

      {/* HEADER */}
      <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between", marginBottom: 20 }}>
        <button onClick={() => window.location.href = "/"}
          style={{ padding: "12px 24px", background: "#333", color: "white", border: "none", borderRadius: 8, cursor: "pointer", fontSize: 16, fontWeight: "bold" }}>
          Back to Dashboard
        </button>
        <h1 style={{ margin: 0, color: "#d32f2f", textAlign: "center", flex: 1 }}>
          LỊCH SỬ PHƯƠNG TIỆN (ADMIN: SỬA / XÓA)
        </h1>
        <div style={{ width: 180 }}></div>
      </div>

      {records.length === 0 ? (
        <div style={{ textAlign: "center", padding: 60, background: "white", borderRadius: 12 }}>
          <p style={{ fontSize: 20, color: "#999" }}>Chưa có dữ liệu nào</p>
        </div>
      ) : (
        <div style={{ overflowX: "auto", background: "white", borderRadius: 12, boxShadow: "0 0 20px rgba(0,0,0,0.15)" }}>
          <table style={{ width: "100%", minWidth: 1300, borderCollapse: "collapse" }}>
            <thead style={{ background: "#d32f2f", color: "white" }}>
              <tr>
                <th style={thStyle}>ID Xe</th>
                <th style={thStyle}>Biển số</th>
                <th style={thStyle}>Loại xe</th>
                <th style={thStyle}>Hướng</th>
                <th style={thStyle}>Thời gian</th>
                <th style={thStyle}>Ảnh toàn cảnh</th>
                <th style={thStyle}>Ảnh biển số</th>
                <th style={thStyle}>Thao tác</th>
              </tr>
            </thead>
            <tbody>
              {records.map(r => (
                <tr key={r.id} style={{ borderBottom: "2px solid #eee", background: r.id % 2 === 0 ? "#fdfdfd" : "white" }}>
                  <td style={tdStyle}>{r.track_id}</td>
                  
                  <td style={tdStyle}>
                    {editingId === r.id ? (
                      <input
                        type="text"
                        value={editPlate}
                        onChange={(e) => setEditPlate(e.target.value)}
                        style={{ padding: 8, fontSize: 18, width: 140, textAlign: "center", border: "2px solid #d32f2f", borderRadius: 6 }}
                        autoFocus
                      />
                    ) : (
                      <b style={{ color: "#d32f2f", fontSize: 22 }}>{r.plate_number}</b>
                    )}
                  </td>

                  {/* Các cột còn lại giữ nguyên như cũ */}
                  <td style={tdStyle}>{r.vehicle_type || "N/A"}</td>
                  <td style={tdStyle}>
                    <span style={{
                      padding: "8px 16px", borderRadius: 30,
                      background: r.direction === "up" ? "#4caf50" : "#f44336",
                      color: "white", fontWeight: "bold"
                    }}>
                      {r.direction?.toUpperCase()}
                    </span>
                  </td>
                  <td style={tdStyle}>{new Date(r.detected_at).toLocaleString("vi-VN")}</td>
                  <td style={tdStyle}>
                    <img src={`http://localhost:5000/${r.full_image_path}`} alt="full"
                      style={{ width: 280, height: 160, objectFit: "cover", borderRadius: 10 }} />
                  </td>
                  <td style={tdStyle}>
                    <img src={`http://localhost:5000/${r.plate_image_path}`} alt="plate"
                      style={{ width: 180, height: 90, objectFit: "contain", border: "4px solid #d32f2f", borderRadius: 10 }} />
                  </td>

                  <td style={{ ...tdStyle, whiteSpace: "nowrap" }}>
                    {editingId === r.id ? (
                      <>
                        <button onClick={() => saveEdit(r.id)}
                          style={{ padding: "8px 16px", background: "#4caf50", color: "white", border: "none", borderRadius: 6, marginRight: 8 }}>
                          Save
                        </button>
                        <button onClick={cancelEdit}
                          style={{ padding: "8px 16px", background: "#999", color: "white", border: "none", borderRadius: 6 }}>
                          Cancel
                        </button>
                      </>
                    ) : (
                      <>
                        <button onClick={() => startEdit(r)}
                          style={{ padding: "8px 16px", background: "#ff9800", color: "white", border: "none", borderRadius: 6, marginRight: 8 }}>
                          Edit
                        </button>
                        <button onClick={() => handleDelete(r.id)}
                          style={{ padding: "8px 16px", background: "#f44336", color: "white", border: "none", borderRadius: 6 }}>
                          Delete
                        </button>
                      </>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* CSS animation cho toast */}
      <style jsx>{`
        @keyframes slideIn {
          from { transform: translateX(100%); }
          to { transform: translateX(0); }
        }
      `}</style>
    </div>
  );
}

const thStyle = { padding: "16px 12px", textAlign: "center", fontSize: 16, fontWeight: "bold" };
const tdStyle = { padding: "16px 10px", textAlign: "center", verticalAlign: "middle" };
